codeExample.advanced:
import Particles from './components/Particles';
import { useMediaQuery } from './hooks/useMediaQuery';

const Hero = () => {
  const isMobile = useMediaQuery('(max-width: 768px)');

  return (
    <div className="relative w-full h-screen bg-[#0A0A0A]">
      <Particles
        particleCount={isMobile ? 150 : 300}
        particleSpread={12}
        speed={0.08}
        particleColors={['#FFD700', '#F0C040', '#FFFFFF']}
        moveParticlesOnHover={!isMobile}
        particleHoverFactor={1.2}
        alphaParticles={true}
        particleBaseSize={isMobile ? 80 : 120}
        cameraDistance={22}
        className="absolute inset-0 z-0 pointer-events-none"
      />
      <div className="relative z-10">{/* Hero content */}</div>
    </div>
  );
};

codeExample.noorDashboards:
import CardSwap, { Card } from './components/CardSwap';
import { useNavigate } from 'react-router-dom';

const DashboardShowcase = () => {
  const navigate = useNavigate();

  const dashboards = [
    { title: 'Individual', route: '/dashboard/individual', color: '#2D8BE0' },
    { title: 'Institutional', route: '/dashboard/institutional', color: '#7A0A0A' },
    { title: 'Federal', route: '/dashboard/federal', color: '#1E3A5F' }
  ];

  return (
    <CardSwap
      width={400}
      height={520}
      cardDistance={50}
      verticalDistance={60}
      delay={5500}
      pauseOnHover={true}
      skewAmount={5}
      easing="elastic"
      onCardClick={(idx) => navigate(dashboards[idx].route)}
    >
      {dashboards.map((dash, i) => (
        <Card key={i} customClass="cursor-pointer hover:shadow-2xl transition-shadow">
          <div className="w-full h-full p-8 flex flex-col" style={{ borderColor: dash.color }}>
            <h3 className="text-2xl font-bold mb-4" style={{ color: dash.color }}>
              {dash.title} Dashboard
            </h3>
            <div className="flex-1 bg-[#1A1A1A] rounded-lg p-4">
              {/* Dashboard preview/screenshot */}
            </div>
          </div>
        </Card>
      ))}
    </CardSwap>
  );
};

