codeExample.withActiveState:
import PillNav from './components/PillNav';
import { useLocation } from 'react-router-dom';
import logo from './assets/noor-logo.svg';

const Navigation = () => {
  const location = useLocation();

  const items = [
    { label: 'About', href: '/about' },
    { label: 'Vision 2071', href: '/vision' },
    { label: 'Contact', href: '/contact' }
  ];

  return (
    <PillNav
      logo={logo}
      logoAlt="NOOR Platform"
      items={items}
      activeHref={location.pathname}
      baseColor="#FFD700"
      pillColor="#0A0A0A"
      hoveredPillTextColor="#0A0A0A"
      pillTextColor="#FFD700"
      ease="power3.easeOut"
      initialLoadAnimation={true}
    />
  );
};

