landingPageHero.codeExample:
import Particles from './components/Particles';
import PillNav from './components/PillNav';
import logo from './assets/noor-logo.svg';

const Hero = () => {
  const navItems = [
    { label: 'About', href: '/about' },
    { label: 'Vision 2071', href: '/vision' },
    { label: 'Contact', href: '/contact' }
  ];

  return (
    <section className="relative w-full h-screen bg-[#0A0A0A] overflow-hidden">
      {/* Navigation - Fixed top */}
      <PillNav
        logo={logo}
        logoAlt="NOOR Platform"
        items={navItems}
        baseColor="#FFD700"
        pillColor="#0A0A0A"
      />

      {/* Particles Background */}
      <Particles
        particleCount={300}
        particleColors={['#FFD700', '#F0C040', '#FFFFFF']}
        alphaParticles={true}
        moveParticlesOnHover={true}
        className="absolute inset-0 z-0 pointer-events-none"
      />

      {/* Hero Content */}
      <div className="relative z-10 h-full flex flex-col items-center justify-center text-center px-4">
        <h1 className="text-[72px] font-[800] text-[#FFD700] mb-4"
            style={{ fontFamily: 'SUSE Mono', textShadow: '0 0 40px rgba(255, 215, 0, 0.4)' }}>
          NOOR
        </h1>
        <p className="text-[48px] text-[#F0C040] mb-8 opacity-80"
           style={{ fontFamily: 'SUSE Mono' }}>
          نور
        </p>
        <p className="text-[20px] text-[#E5E7EB] mb-4 tracking-[0.05em]"
           style={{ fontFamily: 'SUSE Mono' }}>
          National Opportunities Optimization & Realization
        </p>
        <p className="text-[18px] text-[#9CA3AF] mb-12 max-w-[700px] leading-[1.7]"
           style={{ fontFamily: 'SUSE Mono' }}>
          Illuminating human potential through workforce intelligence
        </p>
        <button className="bg-[#FFD700] text-[#0A0A0A] px-12 py-4 rounded-lg text-[18px] font-[600]
                         hover:brightness-110 hover:scale-105 transition-all duration-300"
                style={{ fontFamily: 'SUSE Mono', boxShadow: '0 0 40px rgba(255, 215, 0, 0.3)' }}>
          Discover Your Path →
        </button>
      </div>
    </section>
  );
};

dashboardShowcaseSection.codeExample:
import CardSwap, { Card } from './components/CardSwap';
import { useNavigate } from 'react-router-dom';

const DashboardSection = () => {
  const navigate = useNavigate();

  const dashboards = [
    {
      title: 'Individual',
      subtitle: 'CITIZENS & RESIDENTS',
      route: '/dashboard/individual',
      color: '#2D8BE0',
      features: ['Skills assessment', 'Job matching', 'Learning center']
    },
    {
      title: 'Institutional',
      subtitle: 'EMPLOYERS & ORGANIZATIONS',
      route: '/dashboard/institutional',
      color: '#7A0A0A',
      features: ['EQI tracking', 'Candidate filtering', 'Skills gap analysis']
    },
    {
      title: 'Federal',
      subtitle: 'GOVERNMENT AGENCIES',
      route: '/dashboard/federal',
      color: '#1E3A5F',
      features: ['National analytics', 'Emiratization monitoring', 'KPI dashboards']
    }
  ];

  return (
    <section className="relative w-full min-h-screen bg-[#0A0A0A] py-24 px-4">
      {/* Section Header */}
      <div className="max-w-[1200px] mx-auto mb-16">
        <h2 className="text-[56px] font-[700] text-white text-center mb-4"
            style={{ fontFamily: 'SUSE Mono' }}>
          Choose Your Path
        </h2>
        <p className="text-[18px] text-[#E5E7EB] text-center"
           style={{ fontFamily: 'SUSE Mono' }}>
          Select your role to access the appropriate dashboard
        </p>
      </div>

      {/* Cards Grid - Desktop */}
      <div className="hidden md:grid grid-cols-3 gap-10 max-w-[1200px] mx-auto">
        {dashboards.map((dash, i) => (
          <div key={i}
               className="bg-[#1A1A1A] border-2 border-[#3A3A3A] rounded-2xl p-10
                          hover:border-[var(--color)] hover:-translate-y-2 hover:shadow-2xl
                          transition-all duration-400 cursor-pointer"
               style={{ '--color': dash.color }}
               onClick={() => navigate(dash.route)}>
            <h3 className="text-[32px] font-[700] text-white mb-3"
                style={{ fontFamily: 'SUSE Mono' }}>
              {dash.title}
            </h3>
            <p className="text-[16px] font-[500] mb-6 tracking-[0.1em]"
               style={{ fontFamily: 'SUSE Mono', color: dash.color }}>
              {dash.subtitle}
            </p>
            <ul className="space-y-2 mb-8">
              {dash.features.map((feature, j) => (
                <li key={j} className="text-[14px] text-[#9CA3AF] flex items-start"
                    style={{ fontFamily: 'SUSE Mono' }}>
                  <span className="text-[#E8B86D] mr-2">→</span>
                  {feature}
                </li>
              ))}
            </ul>
            <button className="w-full py-3 border-2 rounded-lg font-[600] text-[16px]
                             hover:bg-[var(--color)] hover:text-[#0A0A0A] transition-all"
                    style={{ fontFamily: 'SUSE Mono', borderColor: dash.color, color: dash.color }}>
              Enter Dashboard →
            </button>
          </div>
        ))}
      </div>

      {/* Card Swap - Mobile/Tablet Alternative */}
      <div className="md:hidden relative h-[600px]">
        <CardSwap
          width={350}
          height={480}
          cardDistance={40}
          verticalDistance={50}
          delay={5000}
          pauseOnHover={true}
          easing="elastic"
          onCardClick={(idx) => navigate(dashboards[idx].route)}
        >
          {dashboards.map((dash, i) => (
            <Card key={i} customClass="cursor-pointer"
                  style={{ borderColor: dash.color }}>
              <div className="w-full h-full p-8 flex flex-col">
                <h3 className="text-[28px] font-[700] text-white mb-2"
                    style={{ fontFamily: 'SUSE Mono' }}>
                  {dash.title}
                </h3>
                <p className="text-[14px] font-[500] mb-6"
                   style={{ fontFamily: 'SUSE Mono', color: dash.color }}>
                  {dash.subtitle}
                </p>
                <div className="flex-1 bg-[#0A0A0A] rounded-lg p-4 border border-[#3A3A3A]">
                  {/* Dashboard preview/illustration */}
                </div>
              </div>
            </Card>
          ))}
        </CardSwap>
      </div>
    </section>
  );
};
